----          'alter system kill session ' || '''' || s.sid || ',' || s.serial# || ',@' || s.inst_id || '''' || ';' kill------ SessÃµes a correr por mto tempo-- RCM 2010/03-- RCM 2011/08-- RCM 2011/09--col inst_id for a4col sid for a6col serial# for a8col opname for a20col target for a20col "Time_remaining" for a25select  		--a.inst_id, a.sid, a.serial#, 		a.opname,        round((sofar*100)/totalwork) as "% CONC",        a.target,        CASE             WHEN a.time_remaining > 3600 THEN round((a.time_remaining/60)/60,2) || ' horas'            WHEN a.time_remaining >   60 THEN round(a.time_remaining/60,2) || ' minutos'            ELSE a.time_remaining || ' segundos'        END   as "Time_remaining"     ,               a.start_time,        elapsed_seconds, message, sql_plan_operation,        sql_plan_optionsfrom    gv$session_longops awhere   /*time_remaining > 0*/ totalwork<>sofarorder by time_remaining desc;