--1. Create SQL Tuning Advisor taskDECLARE  my_task_name VARCHAR2(30);beginmy_task_name := DBMS_SQLTUNE.CREATE_TUNING_TASK(sql_id => '2x5ajqyynfu72',scope => 'COMPREHENSIVE',time_limit => 1200,task_name => 'STA_2x5ajqyynfu72',description => '2x5ajqyynfu72');end;/--2. Run Task EXEC DBMS_SQLTUNE.execute_tuning_task(task_name => 'STA_2x5ajqyynfu72');--3. View results SET LONG 500000SET PAGESIZE 10000SET LINESIZE 200SELECT DBMS_SQLTUNE.REPORT_TUNING_TASK('STA_2x5ajqyynfu72') from dual;-- DROP TaskEXEC DBMS_SQLTUNE.DROP_TUNING_TASK(task_name => 'STA_dap2avktbhd5g');